import * as basicLightbox from "basiclightbox";
const Instance = basicLightbox.create(`
    <div class="modal">
        <p>
            Your first lightbox with just a few lines of code.
            Yes, it's really that simple.
        </p>
    </div>
`);
Instance.show();

export default Instance;
