import { Component } from "react";
import axios from "axios";
import Searchbar from "./Searchbar/Searchbar";

axios.defaults.baseURL = "https://pixabay.com/api/";

const ArticleList = ({ articles }) => (
  <ul>
    {/* {console.log(articles)} */}
    {articles.map(({ id, previewURL, tags }) => (
      <li key={id}>
        <img src={previewURL} alt={tags}></img>
      </li>
    ))}
  </ul>
);

export class App extends Component {
  state = {
    articles: [],
    isLoading: false,
    q: "cat",
    page: 1,
  };

  async componentDidMount() {
    const response = await axios.get(
      `?q=${this.state.q}&page=${this.state.page}&key=25099977-05a832f59cefe7e3a7990f935&image_type=photo&orientation=horizontal&per_page=12`
    );
    this.setState({ articles: response.data.hits });
    // console.log(this.state.articles);
  }
  async componentDidUpdate(_, prevState) {
    if (prevState.q !== this.state.q) {
      let last = this.state.articles;
      const response = await axios.get(
        `?q=${this.state.q}&page=${this.state.page}&key=25099977-05a832f59cefe7e3a7990f935&image_type=photo&orientation=horizontal&per_page=12`
      );
      last.push(response.data.hits);
      this.setState({ articles: last });
    }
  }

  loadMore = () => {
    this.setState((prevState) => ({
      page: prevState.page + 1,
    }));
    // console.log(this.state.page);
  };

  handelChange = (e) => {
    this.setState({
      input: e.target.value,
    });
  };

  render() {
    const { articles } = this.state;

    return (
      <div>
        <Searchbar handelChange={this.handelChange} value={this.state.q} />
        <ArticleList articles={articles} />
        <button onClick={this.loadMore}>loadMore</button>
      </div>
    );
  }
}
